$(document).ready(function(){window.addEventListener("scroll",()=>{const e=document.getElementById("header");300<=window.scrollY?e.classList.add("active"):e.classList.remove("active")});const s=document.querySelector("body"),c=document.querySelector("#burger-menu"),e=document.querySelector("#burger-open"),t=document.querySelector("#burger-close"),o=document.querySelectorAll(".nav-list>li>a, .nav-menu>a");e.addEventListener("click",()=>{c.classList.add("show"),s.classList.add("dis-scroll")}),t.addEventListener("click",()=>{c.classList.remove("show"),s.classList.remove("dis-scroll")}),o.forEach(e=>e.addEventListener("click",()=>{c.classList.remove("show"),s.classList.remove("dis-scroll")}))});